package com.kassandragillmore;import java.util.Scanner;public class Bot {    private String name = "Bot";    Scanner scanner = new Scanner(System.in);    public void welcome() {        System.out.println("Hello, world!\n" +                "My name is " + name + ".\n" +                "It's so nice to meet you.\n" +                "What's your name?");        String playerName = scanner.nextLine();        System.out.println(playerName + "! I've never heard that name before!");    }    public void menu() {        boolean isQuitting = false;        while (!isQuitting) {            System.out.println("So what would you like me to do?");            System.out.println("0 - say goodbye\n" +                    "1 - play guess the number\n" +                    "2 - maths\n" +                    "3 - make a list\n" +                    "4 - tell a joke\n" +                    "5 - write a mad lib story\n");            int input = scanner.nextInt();            switch (input) {                case 0:                    System.out.println("Awww! Hurry back, good bye! :D");                    isQuitting = true;                    break;                case 1:                    GuessingGame.start();                    break;                case 2:                    Maths.start();                    break;                case 3:                    ToDoList.start();                    break;                case 4:                    Joke.start();                    break;                case 5:                    MadLib.start();                    break;                default:                    System.out.println("I do not understand. Try again.");            }        }    }}