package com.kassandragillmore;import java.util.Random;import java.util.Scanner;public class GuessingGame {    public static void start() {        System.out.println("Wanna play a guessing game with me?\n" +                "I'm thinking of a whole number between 1 and 100.\n" +                "What do you think it is?\n" +                "Guess carefully, you only have 10 attempts.");        Random random = new Random();        Scanner scanner = new Scanner(System.in);        int secretNumber = random.nextInt(100);        secretNumber += 1;        int userInput;        int attempts = 0;        do {            userInput = scanner.nextInt();            if (userInput < secretNumber) {                System.out.println("Too low! Try again.");                attempts++;                System.out.println("You have made " + attempts + " attempts.");            } else {                System.out.println("Too high! Try again.");                attempts++;                System.out.println("You have made " + attempts + " attempts.");            }        } while (userInput != secretNumber && attempts != 10);        if (userInput == secretNumber) {            System.out.println("You are correct! You win!");        } else {            System.out.println("You have reached the maximum number of attempts. I win!");        }    }}