package com.kassandragillmore;import java.util.Scanner;public class Maths {    public static void start() {        System.out.println("Let's do some calculations!");        System.out.println("Please pick one.\n" +                "1 - addition\n" +                "2 - subtraction\n" +                "3 - multiplication\n" +                "4 - division\n");        Scanner scanner = new Scanner(System.in);        int input = scanner.nextInt();        switch(input) {            case 1:                System.out.println("What two integers should I add?");                int add1 = scanner.nextInt();                int add2 = scanner.nextInt();                int totalA = addition(add1, add2);                System.out.println(add1 + " + " + add2 + " = " + totalA);                break;            case 2:                System.out.println("What two integers should I subtract?");                int sub1 = scanner.nextInt();                int sub2 = scanner.nextInt();                int totalS = subtract(sub1, sub2);                System.out.println(sub1 + " - " + sub2 + " = " + totalS);                break;            case 3:                System.out.println("What two integers should I multiply?");                int mul1 = scanner.nextInt();                int mul2 = scanner.nextInt();                int totalM = multiply(mul1, mul2);                System.out.println(mul1 + " * " + mul2 + " = " + totalM);                break;            case 4:                System.out.println("What two integers should I divide?");                int div1 = scanner.nextInt();                int div2 = scanner.nextInt();                if(div2 == 0){                    System.out.println("I can not divide by 0.");                    break;                }                int totalD = divide(div1, div2);                System.out.println(div1 + " / " + div2 + " = " + totalD);                break;            default:                System.out.println("I can only perform calculations on integers.");                break;        }        System.out.println("Do you want to do more calculations?\n" +                "1 - yes\n" +                "2 - no");        int quit = scanner.nextInt();        if(quit == 1) {            start();        }        Bot bot = new Bot();        bot.menu();    }    public static int addition(int add1, int add2){        return add1 + add2;    }    public static int subtract(int sub1, int sub2){        return sub1 - sub2;    }    public static int multiply(int mul1, int mul2) {        return mul1 * mul2;    }    public static int divide(int div1, int div2) {        return div1 / div2;    }}