package com.kassandragillmore.madlibs;import java.util.Scanner;public class Story1 {    public static void start () {        Scanner scanner = new Scanner(System.in);        System.out.println("California Dreamin'");        System.out.println("Enter a color:");        String color1 = scanner.nextLine();        System.out.println("Enter another color:");        String color2 = scanner.nextLine();        System.out.println("Pick from weather types: cloudy, sunny, rainy, stormy, snowy");        String weather = scanner.nextLine();        System.out.println("Enter an adjective:");        String adjective1 = scanner.nextLine();        System.out.println("Enter another adjective:");        String adjective2 = scanner.nextLine();        System.out.println("Enter a place:");        String place1 = scanner.nextLine();        System.out.println("Enter another place:");        String place2 = scanner.nextLine();        System.out.println("Enter a verb ending with -ing:");        String verbIng = scanner.nextLine();        System.out.println("Enter a noun:");        String noun1 = scanner.nextLine();        System.out.println("Enter a building:");        String building = scanner.nextLine();        System.out.println("Enter a body part:");        String bodyPart = scanner.nextLine();        System.out.println("Enter a verb:");        String verb = scanner.nextLine();        System.out.println("Enter an occupation:");        String occupation = scanner.nextLine();        System.out.println("Enter a pronoun:");        String pronoun = scanner.nextLine();        System.out.println("Enter another place:");        String place3 = scanner.nextLine();        System.out.println("Enter another verb ending in -ing:");        String verbIng2 = scanner.nextLine();        System.out.println("Enter another noun:");        String noun2 = scanner.nextLine();        System.out.println("All the leaves are " + color1 + "\n" +                "and the sky is " + color2 + "\n" +                "I've been for a walk on a " + weather + " day\n" +                "I'd be " + adjective1 + " and " + adjective2 + "\n" +                "If I was in " + place1 + "\n" +                place2 + " " + verbIng + " on such a winter's " + noun1 + "\n" +                "Stopped into a " + building + "\n" +                "I passed along the way\n" +                "Well, I got down on my " + bodyPart + "\n" +                "and I pretend to " + verb + "\n" +                "You know the " + occupation + "s like the cold\n" +                pronoun + " knows I'm gonna stay\n" +                place3 + " " + verbIng2 + "\n" +                "on such a winter's " + noun2);    }}